import {useEffect} from "react";
import axios from "axios";
import {Button} from "primereact/button";
import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import {TabMenu} from "primereact/tabmenu";
import {useState} from "react";

export const Home1 = () => {
    const [activeIndex, setActiveIndex] = useState(3);
    const [dataBrand, setDataBrand] = useState([]);

    const items = [
        {label: 'Brand', icon: 'pi pi-fw pi-home'},
        {label: 'Calendar', icon: 'pi pi-fw pi-calendar'},
        {label: 'Edit', icon: 'pi pi-fw pi-pencil'},
        {label: 'Documentation', icon: 'pi pi-fw pi-file'},
        {label: 'Settings', icon: 'pi pi-fw pi-cog'}
    ];
    useEffect(() => {
        axios.get('http://localhost:3030/mv-core/v1/admin/brand')
            .then(res => setDataBrand(res.data))
            .catch(err => console.log(err))

    }, [])
    const paginatorLeft = <Button type="button" icon="pi pi-refresh" text />;
    const paginatorRight = <Button type="button" icon="pi pi-download" text />;
    const header = renderHeader();

    return (
        <div className="card">
            <Button onClick={() => setActiveIndex(0)} className="p-button-outlined mb-5" label="Activate 1st" />
            <TabMenu model={items} activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)} />
            {activeIndex==0?
                <div className="card">
                    <DataTable value={dataBrand?.data} paginator rows={5} rowsPerPageOptions={[5, 10, 25, 50]} tableStyle={{ minWidth: '50rem' }}
                               paginatorTemplate="RowsPerPageDropdown FirstPageLink PrevPageLink CurrentPageReport NextPageLink LastPageLink"
                               currentPageReportTemplate="{first} to {last} of {totalRecords}" paginatorLeft={paginatorLeft} paginatorRight={paginatorRight}>
                        <Column field="name" header="Name" style={{ width: '25%' }}></Column>
                        <Column field="description" header="Description" style={{ width: '25%' }}></Column>
                        <Column  header="operation" style={{ width: '25%' }}></Column>
                    </DataTable>
                </div>:""
            }
        </div>
    )
}
